    mvwprintw(stdscr,3+ 10,xMax / 4 + 6 + (lmaxx ) / 2  - 74,"|       |    | |_____   |   |  |  | |  ||   |_||_ |       |    |  | |  ||   |___     |  | |  ||       ||   |___     |   |___ |   | | |_____ |       |\n");
