#include "header.h"

void start(int row) {
int counter = 0;

    while(counter != row) {
        mvprintw(counter,0,"");
        counter++;
        refresh();
        usleep(1000*20);
    }
    clear();
    while(counter != 0) {
        mvprintw(counter,0,":");
        counter--;
        refresh();
        usleep(1000*20);
    }
    clear();
}
